	public long generateInspNo(int roCode, String empCode) {

		long inspNo = spotTranHeaderDAO.getInspNo(roCode, empCode);
		if (inspNo > 0) {
			return -1;// Means same RO was inspected by same officer in last 7 days.
		} else {
			return spotTranHeaderDAO.getInspNo();
		}
	}

	public boolean saveSpotTranData(SpotCheckPayloadData data, String user) {

		SpotTranHeader header = new SpotTranHeader();
		String userLevel;
		ArrayList<SpotTranAnswer> answers = new ArrayList<>();
		long inspNo = generateInspNo(data.getRoCode(), user);
		if (inspNo == -1) {
			return false;
		}
		EmployeeMaster emp = employeeMasterService.getEmployeeMaster(Integer.parseInt(user));

		if (emp.getSalesGroup() != null && !emp.getSalesGroup().equalsIgnoreCase("N/A")
				&& emp.getPsaCode().equalsIgnoreCase("SL01")) {
			userLevel = "FO";
		} else {
			userLevel = empJurService.getUserLevelByPaAndPsa(emp.getPaCode().substring(0, 2), emp.getPsaCode());
		}
		header.setInspNo(inspNo);
		header.setRoCode(data.getRoCode());
		header.setUserDetails(employeeMasterService.getUserDetails(emp));
		header.setUserLevel(userLevel);
		spotTranHeaderDAO.save(header);

		data.getSpotCheckPoints().forEach(point -> {
			if (point.getSpotCheckPointValue() != null && !"".equals(point.getSpotCheckPointValue().trim())) {
				SpotTranAnswer answer = new SpotTranAnswer();
				answer.setId(
						new SpotTranAnswerId(header.getInspNo(), point.getSpotCheckPointModuleNo(), point.getSpotCheckPointNo()));
				answer.setAnswer(point.getSpotCheckPointValue());
				// answer.setResponsible(point.getSpotCheckPointResponsible());
				// answer.setUserDetails(emMasterService.getUserDetails(Integer.parseInt(user)));
				// spotTranAnswerDAO.save(answer);

				if ("Yes".equals(point.getSpotCheckPointValue())) {
					answer.setStatus(400);
				} else {
					if ("I".equals(point.getSpotCheckPointResponsible())) {
						answer.setStatus(200);
					} else if ("D".equals(point.getSpotCheckPointResponsible())) {
						answer.setStatus(100);
					} else {
						answer.setStatus(0);
					}
				}
				answers.add(answer);
			}
		});

		// header.setAnswers(answers);
		spotTranAnswerDAO.saveAll(answers);
		return true;
	}

