	public ArrayList<SpotCheckComplianceFO> getSpotCheckComplianceFO(int empCode) {

		EmployeeMaster employeeMaster = employeeMasterService.getEmployeeMaster(empCode);

		if (employeeMaster != null && employeeMaster.getSalesGroup() != null
				&& !employeeMaster.getSalesGroup().trim().equals("")) {
			return spotCheckComplianceFODAO.findBySalesAreaCode(employeeMaster.getSalesGroup());
		} else {
			return new ArrayList<>();
		}
	}

	public ArrayList<SpotCheckTranData> getSpotCheckCompliantPoints(long inspNo, int status) {
		return spotCheckTranDataDAO.findByInspNoAndStatus(inspNo, status);
	}

	public int saveFOComplianceData(ComplianceResponsePayload payload, String user) {

		SpotCheckAtrID atrId = new SpotCheckAtrID(payload.getInspNo(), payload.getModuleNo(), payload.getQuestionNo());
		SpotCheckAtr spotCheckAtr = new SpotCheckAtr(atrId, payload.getRemark(),
				employeeMasterService.getUserDetails(Integer.parseInt(user)), "FO", new Date());
		spotCheckAtrDAO.save(spotCheckAtr);

		SpotTranAnswerId tranId = new SpotTranAnswerId(payload.getInspNo(), payload.getModuleNo(), payload.getQuestionNo());

		SpotTranAnswer tranAnswer = spotTranAnswerDAO.findById(tranId).orElse(new SpotTranAnswer());
		tranAnswer.setStatus(300);
		spotTranAnswerDAO.save(tranAnswer);

		return 1;
	}

	public ArrayList<SpotCheckComplianceDRSM> getSpotCheckComplianceDRSM(int empCode) {

		EmployeeMaster employeeMaster = employeeMasterService.getEmployeeMaster(empCode);
		String userLevel = empJurService.getUserLevelByPaAndPsa(employeeMaster.getPaCode().substring(0, 2),
				employeeMaster.getPsaCode());

		if (employeeMaster != null && employeeMaster.getLocInFlag() != null
				&& !employeeMaster.getLocInFlag().trim().equals("") && "DO".equals(userLevel)) {
			return spotCheckComplianceDRSMDAO.findBySalesAreaCode(employeeMaster.getSalesGroup());
		} else {
			return new ArrayList<>();
		}
	}

	public int saveDRSMComplianceData(ComplianceResponsePayload payload, String user) {

		SpotCheckAtrID atrId = new SpotCheckAtrID(payload.getInspNo(), payload.getModuleNo(), payload.getQuestionNo());
		SpotCheckAtr spotCheckAtr = new SpotCheckAtr(atrId, payload.getRemark(),
				employeeMasterService.getUserDetails(Integer.parseInt(user)), "DRSM", new Date());
		spotCheckAtrDAO.save(spotCheckAtr);

		SpotTranAnswerId tranId = new SpotTranAnswerId(payload.getInspNo(), payload.getModuleNo(), payload.getQuestionNo());

		SpotTranAnswer tranAnswer = spotTranAnswerDAO.findById(tranId).orElse(new SpotTranAnswer());
		tranAnswer.setStatus(400);
		spotTranAnswerDAO.save(tranAnswer);

		return 1;
	}

	public String checkSpotCheckEligibility(String user) {

		ArrayList<StoredProcedureParameter> paramList = new ArrayList<>();
		paramList.add(new StoredProcedureParameter("in_EMP_ID", Integer.class, ParameterMode.IN, Integer.parseInt(user)));

		String response = (String) genericProcedureCallDAO.callProcAndReturnOutParam("PROC_SPOT_CHECK_INTERLOCK",
				"out_message", String.class, paramList);

		return response;
	}
